import React from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

import $ from 'jquery';

class Menu extends React.Component{
    constructor(props) {
      super(props);

    //   this.OrderClick = this.OrderClick.bind(this);
    //   this.PaybillsClick = this.PaybillsClick.bind(this);
    //   this.ForecastClick = this.ForecastClick.bind(this);
    }

    // OrderClick(e){
    //     e.preventDefault(e);
    //     this.props.OrderClick(e.target.value);
    // }

    // PaybillsClick(e){
    //     e.preventDefault(e);
    //     this.props.PaybillsClick(e.target.value);
    // }

    // ForecastClick(e){
    //     e.preventDefault(e);
    //     this.props.ForecastClick(e.target.value);
    // }

    render(){


        return(
        <Router>
            <div class="menu_page">
                <div class="menu_category">
                    <h2>類別</h2>
                    <ul>
                        <li><Link to="/menu/normal_food">單/雙拼</Link></li>
                        <li><Link to="/menu/special_food">特餐</Link></li>
                        <li><Link to="/menu/drink">飲品</Link></li>
                        <li><Link to="/menu/hotpot">火鍋配料</Link></li>
                        <li><Link to="/menu/other">其他</Link></li>
                    </ul>
                </div>
                <Switch>
                    <Route exact path="/menu/normal_food">
                        <Normal_food />
                    </Route>
                    <Route path="/menu/special_food">
                        <Special_food />
                    </Route>
                    <Route path="/menu/drink">
                        <Drink />
                    </Route>
                    <Route path="/menu/hotpot">
                        <Hotpot />
                    </Route>
                    <Route path="/menu/other">
                        <Other />
                    </Route>
                </Switch>
                <div class="added_item">
                    <div class="items_added">
                        <img src="./images/food1.jpg" width="100" height="100"></img>
                        <div>Product Name</div>
                        <div>Price</div>
                    </div>
                    <button> 提交</button>
                </div>


            </div>
        </Router>
        );
    }
}

function Normal_food() {
    return (
        <div class="menu_item">
        <div class="items_name">
            <p>請選擇一或兩樣</p>
        </div>
        <div class="items">
            <img src="./images/food1.jpg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items">
            <img src="./images/food2.jpeg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items">
            <img src="./images/food3.jpg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items_name">
            <p>請選擇撈麵/飯/泡飯</p>
        </div>

        <div class="items">
            <img src="./images/food1.jpg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items_name">
            <p>跟餐加配</p>
        </div>

        <div class="items">
            <img src="./images/food1.jpg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items_name">
            <p>飲品</p>
        </div>

        <div class="items">
            <img src="./images/food1.jpg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items_name">
            <p>Customerised</p>
        </div>

        <div class="items">
            <img src="./images/food1.jpg" width="100" height="100"></img>
            <div>Product Name</div>
        </div>

        <div class="items_name">
            <p>學生優惠</p>
        </div>
    </div>
    );
  }

  function Special_food() {
    return (
        <div class="menu_item">
        <div class="items">
            <img src="./images/food1.jpg" width="100" height="100"></img>
            <div>Product Name</div>
            <div>Price</div>
        </div>

        <div class="items">
            <div>Img</div>
            <div>Product Name</div>
            <div>Price</div>
        </div>

