import React from 'react';
import logo from './logo.svg';
import './App.css';
import Try from './Try';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

// import Logins from "./components/login";

class App extends React.Component{
  constructor(props) {
    super(props);

    this.state = {
      isLoggedIn: false,
      username: '',
      password: '',
      login_fail: '',
    };
    this.usernameChange = this.usernameChange.bind(this);
    this.passwordChange = this.passwordChange.bind(this);
    this.LoginClick = this.LoginClick.bind(this);
  }

  render(){
    const isLoggedIn = this.state.isLoggedIn;
    let current_page, login;

    if (isLoggedIn) {
      current_page = <MainPage
      username={this.state.username}


      />;
    }
    else{
      current_page = <Login
      username={this.state.username}
      password={this.state.password}
      onUsernameChange={this.usernameChange}
      onPasswordChange={this.passwordChange}
      LoginClick={this.LoginClick}
      login_fail={this.state.login_fail}
      />;

    }

    return (
     <div className="App">
       {current_page}
      </div>

    );
  }

  usernameChange(name) {
    this.setState({
      username: name
    })
  }

  passwordChange(pass) {
    this.setState({
      password: pass
    })
  }

  componentDidMount(){
    if(localStorage.loggin === 'true'){
      this.handlePageRefresh(localStorage.usrname, localStorage.password);
    }
  }

  handlePageRefresh(usrname, password){
    var url = "http://localhost:3001/login";
    var user = {'username': usrname, 'password': password};
    $.ajax({
      url: url,
      method: "POST",
      data: user,
      xhrFields: {
        withCredentials: true
      },
      success:function(data){
        if (data.message === "Login Success!"){
          // this.props.SuccessLogin(data.login_usr, data.data);
          this.setState({
            isLoggedIn: true,
            login_fail: '',
          })
        } else{
          this.setState({
            username: '',
            password: '',
            isLoggedIn: false,
            login_fail: data.error,
          })
        }
      }.bind(this),
      error:function (xhr, ajaxOptions, thrownError) {
        alert(xhr.status);
        alert(thrownError);
      }
    });
  }

  LoginClick(e) {
    if (this.state.username === '' || this.state.password === '') { 
      alert('Please fill in all fields');
    }
    else{
      localStorage.setItem('usrname', this.state.username);
      localStorage.setItem('password', this.state.password);

      var loginInfo = {
        "username" : this.state.username,
        "password" : this.state.password,
      }

      $.ajax({
        url: "http://localhost:3001/login",
        type: 'POST',
        data: loginInfo,
        xhrFields: { withCredentials: true },
        // dataType: 'JSON',
        success: function(data) {
          if (data.message === "Login Success!"){
            localStorage.setItem('loggin','true');
            // this.props.SuccessLogin(data.login_usr, data.data);
            this.setState({
              isLoggedIn: true,
              login_fail: '',
            });
          } else{
            this.setState({
              username: '',
              password: '',
              isLoggedIn: false,
              login_fail: "Login Fail"
            });
          }
        }.bind(this), 
          error: function (xhr, ajaxOptions, thrownError) {
            alert(xhr.status);
            alert(thrownError);
          }
      });

  }

  e.preventDefault(e);
  }

}

class Login extends React.Component {
  constructor(props) {
    super(props);

   this.usernameChange = this.usernameChange.bind(this);
   this.passwordChange = this.passwordChange.bind(this);
   this.LoginClick = this.LoginClick.bind(this);
  //  this.textInput = null;

 }

  usernameChange(e) {
    e.preventDefault(e);
    this.props.onUsernameChange(e.target.value);
  }

  passwordChange(e) {
    e.preventDefault(e);
    this.props.onPasswordChange(e.target.value);
  }

  LoginClick(e) {
    e.preventDefault(e);
    this.props.LoginClick(e);
  }

  render() {
      return (
        <div >
          <div id="logo_title">SUN PO</div>
          <form class="login_form">
              <h3>Login</h3>

              <p id="error_message"> {this.props.login_fail} </p>

              <div className="form-group">
                  <label>Username</label>
                  <input type="text" className="form-control" placeholder="Enter username" value={this.props.username}
                  onChange={this.usernameChange}/>
              </div>

              <div className="form-group">
                  <label>Password</label>
                  <input type="password" className="form-control" placeholder="Enter password" value={this.props.password}
                  onChange={this.passwordChange}/>
              </div>

              <button type="button" className="btn btn-primary btn-block" onClick={this.LoginClick}>Submit</button>
            </form>
          </div>
      );
  }
}

class MainPage extends React.Component{
  constructor(props) {
    super(props);


 }

  render() {

    return (
      <div>
         <Try />
        <div id="top">
          <div class="inline" >
              {/* <img class="inline" src='MyApp/public/{this.props.icon}' height="50" width="50"/>  */}
              <p class="inline" id="name">{this.props.username}</p>
          </div>
          <button id="logout" >Log out</button>
        </div>

        <p class="col-left">Starred Friend List: </p>


    </div>

    );
  }
}



export default App;
