class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      login_fail: '',
      isLoggedIn: false,
    }
   this.usernameChange = this.usernameChange.bind(this);
   this.passwordChange = this.passwordChange.bind(this);
   this.LoginClick = this.LoginClick.bind(this);
  //  this.textInput = null;

 }

 usernameChange(name) {
  this.setState({
    username: name.target.value
  })
}

passwordChange(pass) {
  this.setState({
    password: pass.target.value
  })
}
LoginClick(e) {
  e.preventDefault();
  if (this.state.username === '' || this.state.password === '') { 
    alert('Please fill in all fields');
  }
  else{
    localStorage.setItem('usrname', this.state.username);
    localStorage.setItem('password', this.state.password);

    var loginInfo = {
      "username" : this.state.username,
      "password" : this.state.password,
    }

    $.ajax({
      url: "http://localhost:3001/login",
      type: 'POST',
      data: loginInfo,
      xhrFields: { withCredentials: true },
      dataType: 'JSON',
      success: function(data) {
        if (data.message === "Login Success!"){
          localStorage.setItem('loggin','true');
          // this.props.SuccessLogin(data.login_usr, data.data);
          this.setState({
            username: '',
            password: '',
            isLoggedIn: true,
            login_fail: '',
          });
        } else{
          this.setState({
            username: 'asdas',
            password: '',
            isLoggedIn: false,
            login_fail: "Login Fail"
          });
        }
      }.bind(this), 
        error: function (xhr, ajaxOptions, thrownError) {
           alert(xhr.status);
            alert(thrownError);
        }
    });

}

}

  render() {
      return (
        <div >
          <div id="logo_title">SUN PO</div>
          <form class="login_form" onSubmit={this.LoginClick}>
              <h3>Login</h3>

              <p> {this.state.login_fail} </p>

              <div className="form-group">
                  <label>Username</label>
                  <input type="text" className="form-control" placeholder="Enter username" value={this.state.username}
