  MinusClick(data, e) {
    this.state.orderList.map(item => {
      if (item.name == data) {
        item.quantity = item.quantity - 1;
        if (item.quantity < 1) item.quantity = 1;
      }
    });
    setTimeout(function () {
      this.setState({
        addedList: this.state.orderList
      });
    }.bind(this), 100);
  }

  PlusClick(data, e) {
    this.state.orderList.map(item => {
      if (item.name == data) {
        item.quantity = item.quantity + 1;
      }
    });
    setTimeout(function () {
      this.setState({
        addedList: this.state.orderList
      });
    }.bind(this), 100);
  }

