            this.props.OrderTableClick(e.target.value, "2" + data);
        }
        if(this.props.payBills === '點餐'){
            // alert("Delete?");
            this.props.OrderTableClick(e.target.value, "1" + data);
        }
    }     

    ViewCurrOrder(data, e){
        alert('View?' + data);
    }   

    DeleteCurrOrder(data, e){
        // alert('Delete?' + data);
