import React from 'react';
import logo from './logo.svg';
import './App.css';
// import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

import Logins from "./components/login";

class App extends React.Component{
  constructor(props) {
    super(props);

    this.state = {
      isLoggedIn: false,
      username: '',
      password: '',
    };
    this.usernameChange = this.usernameChange.bind(this);
    this.passwordChange = this.passwordChange.bind(this);
    this.LoginClick = this.LoginClick.bind(this);
  }

  render(){
    const isLoggedIn = this.state.isLoggedIn;
    let current_page, login;

    if (isLoggedIn) {
      current_page = <MainPage
      username={this.state.username}


      />;
    }
    else{
      current_page = <Login
      username={this.state.username}
      password={this.state.password}
      onUsernameChange={this.usernameChange}
      onPasswordChange={this.passwordChange}
      LoginClick={this.LoginClick}
      />;
      // login =  <Router>
      // <div className="App">
      //   <nav className="navbar navbar-expand-lg navbar-light fixed-top">
      //     <div className="container">
      //       <Link className="navbar-brand" to={"/sign-in"}>positronX.io</Link>
      //       <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
      //         <ul className="navbar-nav ml-auto">
      //           <li className="nav-item">
      //             <Link className="nav-link" to={"/sign-in"}>Login</Link>
      //           </li>
      //         </ul>
      //       </div>
      //     </div>
      //   </nav>

      //   <div className="auth-wrapper">
      //     <div className="auth-inner">
      //       <Switch>
      //         <Route exact path='/' component={Logins} />
      //         <Route path="/sign-in" component={Logins} />
      //       </Switch>
      //     </div>
      //   </div>
      // </div></Router>
    }

    return (
     <div className="App">
       {/* {current_page} */}
       {/* {login} */}
      </div>

    );
  }

  usernameChange(name) {
    this.setState({
      username: name
    })
  }

  passwordChange(pass) {
    this.setState({
      password: pass
    })
  }

  LoginClick(e) {
    e.preventDefault(e);

    if (this.state.username === '' || this.state.password === '') { 
      alert('Please fill in all fields');
    }
    else{
      var loginInfo = {
        "username" : this.state.username,
        "password" : this.state.password,
      }

      $.ajax({
        url: "http://localhost:3001/login",
        type: 'POST',
        data: loginInfo,
        xhrFields: { withCredentials: true },
        dataType: 'JSON',
        success: function(data) {
          this.setState({
            isLoggedIn: true,
          });
        }.bind(this), 
          error: function (xhr, ajaxOptions, thrownError) {
             alert(xhr.status);
              alert(thrownError);
          }.bind(this)
      });

  }


  }

}

class Login extends React.Component {
  constructor(props) {
    super(props);

   this.usernameChange = this.usernameChange.bind(this);
   this.passwordChange = this.passwordChange.bind(this);
   this.LoginClick = this.LoginClick.bind(this);
  //  this.textInput = null;

 }

  usernameChange(e) {
    e.preventDefault(e);
    this.props.onUsernameChange(e.target.value);
  }

  passwordChange(e) {
    e.preventDefault(e);
    this.props.onPasswordChange(e.target.value);
  }

  LoginClick(e) {
    e.preventDefault(e);
    this.props.LoginClick(e);
  }

  render() {
      return (
          <form>
              <h3>Login</h3>

              <div className="form-group">
                  <label>Username</label>
                  <input type="text" className="form-control" placeholder="Enter username" value={this.props.username}
                  onChange={this.usernameChange}/>
              </div>

              <div className="form-group">
                  <label>Password</label>
                  <input type="password" className="form-control" placeholder="Enter password" value={this.props.password}
                  onChange={this.passwordChange}/>
              </div>

              <button type="button" className="btn btn-primary btn-block" onClick={this.LoginClick}>Submit</button>
          </form>
      );
  }
}

class MainPage extends React.Component{
  constructor(props) {
    super(props);


 }

  render() {

    return (
      <div>
        <div id="top">
          <div class="inline" >
              {/* <img class="inline" src='MyApp/public/{this.props.icon}' height="50" width="50"/>  */}
              <p class="inline" id="name">{this.props.username}</p>
          </div>
          <button id="logout" >Log out</button>
        </div>

        <p class="col-left">Starred Friend List: </p>


    </div>
    );
  }
}



export default App;
