import React from 'react';

import Menu from './Menu';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";

import $ from 'jquery';
import { timingSafeEqual } from 'crypto';


class TableList extends React.Component{
    constructor(props) {
      super(props);

      this.state = {
        transinfo: [],
        tableinfo: [{'tableNumber': '1','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction':[]}, 
      {'tableNumber': '2','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction': []},
      {'tableNumber': '3','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction': []}, 
      {'tableNumber': '4','seatTaken': 0, 'maxCap': 6, 'isFull': false, 'tableColor': '', 'transaction': []}, 
      {'tableNumber': '5','seatTaken': 0, 'maxCap': 6, 'isFull': false, 'tableColor': '', 'transaction': []},
      {'tableNumber': '6','seatTaken': 0, 'maxCap': 6, 'isFull': false, 'tableColor': '', 'transaction': []}, 
      {'tableNumber': '7','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction': []}, 
      {'tableNumber': '8','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction': []},
      {'tableNumber': '9','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction': []}, 
      {'tableNumber': '10','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': '', 'transaction':[]}],
      };

    //   this.TableClick = this.TableClick.bind(this);
    }

    componentDidMount(){

        //get transaction
        $.ajax({
            url:"http://localhost:3001/gettransaction",
            method:"GET",
            xhrFields: {
              withCredentials: true
            },
            success: function(datas){
                if (datas.message === "Login Success!"){
                    alert("success");
                }
                // alert(datas.tran[0].products[0].name+" "+datas.tran[0].products[0].price+" "+datas.tran[0].products[0].originalPrice+" "+
                // datas.tran[0].products[0].quantity+" "+datas.tran[0].products[0].custom[0]+" "+datas.tran[0].totalPrice+' '+
                // datas.tran[0].tranId+" "+datas.tran[0].tableNumber+" "+datas.tran[0].transTime+" "+datas.tran[0].orderStatus+" "+datas.tran[0].seatTaken);
                for (var i in datas.tran){
                    this.setState({
                        transinfo: this.state.transinfo.concat({"tranId": datas.tran[i].tranId, "userId": datas.tran[i].userId, "products": datas.tran[i].products, 
                        "totalPrice": datas.tran[i].totalPrice, "tableNumber": datas.tran[i].tableNumber, 
                        "orderStatus": datas.tran[i].orderStatus, "transTime": datas.tran[i].transTime, "seatTaken": datas.tran[i].seatTaken,}),
                      });
                }
            }.bind(this),
            error: function (xhr, ajaxOptions, thrownError) {
              alert(xhr.status);
              alert(thrownError);
            }
          });

        setTimeout(function() { 
            // this.state.transinfo[0].products.map((tran, index) => {
            //     alert(tran.name+' '+tran.custom[0]);
            // })

            this.state.tableinfo.map((table, index) => {
                this.state.transinfo.map((tran, i) => {
                    if(tran.tableNumber === table.tableNumber){
                        let items = [...this.state.tableinfo];
                        let item = {...items[index]};
                        item.seatTaken = item.seatTaken + tran.seatTaken;
                        item.transaction = item.transaction.concat(tran.tranId);
                        items[index] = item;
                        this.setState({tableinfo: items});
                    }
                })
            })

            this.state.tableinfo.map((table, index) => {

                if(table.maxCap === table.seatTaken){
                    // alert("maxcap: "+table.maxCap+" seatTaken: "+table.seatTaken);
                    let items = [...this.state.tableinfo];
                    let item = {...items[index]};
                    item.isFull = true;
                    items[index] = item;
                    this.setState({tableinfo: items});
                }
            })

            this.state.tableinfo.map((table, index) => {
                let items = [...this.state.tableinfo];
                let item = {...items[index]};
                if(table.isFull === true){
                    item.tableColor = 'red';
                }
                else if(table.isFull !== true && table.seatTaken > 0){
                    item.tableColor = 'orange';
                }
                else{
                    item.tableColor = 'palegreen';
                }
                items[index] = item;
                this.setState({tableinfo: items});
                // alert(table.tableNumber+' '+table.tableColor);


                if(table.transaction && table.transaction.length > 0){
                    // alert("Table "+table.tableNumber + " has transaction");
                    // alert(table.transaction[0]);
                }
            }

            )
        }.bind(this), 100)
    }

    TableClick(data, e){
        let which_function;
        if(this.props.payBills === '結賬'){
            which_function = "2" + data;
        }
        else{
            which_function = "1" + data;
        }

        if(!this.state.tableinfo[parseInt(data)-1].isFull){
            this.props.TableClick(e.target.value, which_function);
        }

    }     

    // TClick = (e, data) => {
    //     alert('Clicked Table ' + e);
    // } 


    render(){
        let current, functions;
    functions = <h3 className="table_title" >{this.props.payBills}</h3>
        // current = 

        return(
            <div class="table_page">
                {functions}
                {/* {current} */}
                <div class="tables_background">
            <div class="table_column">
                <div style={{backgroundColor: this.state.tableinfo[9].tableColor}} id="table_10" class="rectangle tables" onClick={this.TableClick.bind(this, "10")}>
                    <ul>
                        <li>Table 10</li>
                        <li>使用率: {this.state.tableinfo[9].seatTaken}/{this.state.tableinfo[9].maxCap}</li>
                        {this.state.tableinfo.map(item => (
                            item.map(i => (
                                <li>{i.transaction}</li>
                            ))
                        ))}
                        <li>{this.state.tableinfo[9].transaction[0]}</li>
                    </ul>
                </div>
                <div style={{backgroundColor: this.state.tableinfo[8].tableColor}} id="table_9" class="rectangle tables" onClick={this.TableClick.bind(this, "9")}>
                <ul>
                    <li>Table 9</li>
                    <li>使用率: {this.state.tableinfo[8].seatTaken}/{this.state.tableinfo[8].maxCap}</li>
                    <li>{this.state.tableinfo[8].transaction[0]}</li>
                    <li>{this.state.tableinfo[8].transaction[0]}</li>
                </ul>
                </div>
                <div style={{backgroundColor: this.state.tableinfo[7].tableColor}} id="table_8" class="rectangle tables" onClick={this.TableClick.bind(this, "8")}>Table 8</div>
                <div style={{backgroundColor: this.state.tableinfo[6].tableColor}} id="table_7" class="rectangle tables" onClick={this.TableClick.bind(this, "7")}>Table 7</div>
            </div>

            <div class="table_column table_col2">
                <div class="rectangle2" ></div>
                <div style={{backgroundColor: this.state.tableinfo[5].tableColor}} id="table_6" class="circle tables" onClick={this.TableClick.bind(this, "6")}>Table 6</div>
                <div style={{backgroundColor: this.state.tableinfo[4].tableColor}} id="table_5" class="circle tables" onClick={this.TableClick.bind(this, "5")}>Table 5</div>
                <div style={{backgroundColor: this.state.tableinfo[3].tableColor}} id="table_4" class="circle tables" onClick={this.TableClick.bind(this, "4")}>Table 4</div>
            </div>

            <div class="table_column">
                <div class="rectangle2" ></div>
                <div style={{backgroundColor: this.state.tableinfo[2].tableColor}} id="table_3" class="rectangle tables" onClick={this.TableClick.bind(this, "3")}>Table 3</div>
                <div style={{backgroundColor: this.state.tableinfo[1].tableColor}} id="table_2" class="rectangle tables" onClick={this.TableClick.bind(this, "2")}>Table 2</div>
                <div style={{backgroundColor: this.state.tableinfo[0].tableColor}} id="table_1" class="rectangle tables" onClick={this.TableClick.bind(this, "1")}>Table 1 a {this.state.tableinfo[0].transaction[0]}</div>
                {/* <div style={{backgroundColor: 'palegreen'}} id="table_1" class="rectangle" onClick={(e) => this.TClick("1", e)}>Table 1</div> */}
            </div>
        </div>
            </div>

        );
    }
}

export default TableList;
