import React from 'react';

import Menu from './Menu';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";

import $ from 'jquery';
import { timingSafeEqual } from 'crypto';


class TableList extends React.Component{
    constructor(props) {
      super(props);

      this.state = {
        // tableinfo: [{table_number: '1'},]
        tableinfo: [{'tableNumber': '1','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': 'palegreen', 'transaction':[{"product": "as"}]}, 
      {'tableNumber': '2','seatTaken': 0, 'maxCap': 4, 'isFull': true, 'tableColor': 'palegreen'},
      {'tableNumber': '3','seatTaken': 0, 'maxCap': 4, 'isFull': false, 'tableColor': 'palegreen'}, 
      {'tableNumber': '4','seatTaken': 0, 'maxCap': 6, 'isFull': false, 'tableColor': 'palegreen'}, 
      {'tableNumber': '5','seatTaken': 0, 'maxCap': 6, 'isFull': false,  'tableColor': 'palegreen'},
      {'tableNumber': '6','seatTaken': 0, 'maxCap': 6, 'isFull': false, 'tableColor': 'palegreen'}, 
      {'tableNumber': '7','seatTaken': 0, 'maxCap': 4, 'isFull': false,  'tableColor': 'palegreen'}, 
      {'tableNumber': '8','seatTaken': 0, 'maxCap': 4, 'isFull': false,  'tableColor': 'palegreen'},
      {'tableNumber': '9','seatTaken': 0, 'maxCap': 4, 'isFull': false,  'tableColor': 'palegreen'}, 
      {'tableNumber': '10','seatTaken': 0, 'maxCap': 4, 'isFull': false,  'tableColor': 'palegreen'}],
      };

    //   this.TableClick = this.TableClick.bind(this);
    }

    componentDidMount(){



        setTimeout(function() { 

            this.state.tableinfo.map((table, index) => 
            {
                if(table.isFull === true){
                    let items = [...this.state.tableinfo];
                    let item = {...items[index]};
                    item.tableColor = 'red';
                    items[index] = item;
                    this.setState({tableinfo: items});

                    // alert(table.tableNumber+' '+table.tableColor);
                }

            }

            )
        }.bind(this), 100)
    }

    TableClick(data, e){
        // alert('Clicked ' + data);
        // e.preventDefault(e);
        let which_function;
        if(this.props.payBills === '查閱'){
            which_function = "2" + data;
        }
        else{
            which_function = "1" + data;
        }
        this.props.TableClick(e.target.value, which_function);
        // alert(which_function);
    }     

    // TClick = (e, data) => {
    //     alert('Clicked Table ' + e);
    // } 


    render(){
        let current, functions;
    functions = <h1>{this.props.payBills}</h1>
        current = 
        <div >
            <div class="table_column">
                <div style={{backgroundColor: this.state.tableinfo[9].tableColor}} id="table_10" class="rectangle" onClick={this.TableClick.bind(this, "10")}>Table 10</div>
                <div style={{backgroundColor: this.state.tableinfo[8].tableColor}} id="table_9" class="rectangle" onClick={this.TableClick.bind(this, "9")}>Table 9</div>
                <div style={{backgroundColor: this.state.tableinfo[7].tableColor}} id="table_8" class="rectangle" onClick={this.TableClick.bind(this, "8")}>Table 8</div>
                <div style={{backgroundColor: this.state.tableinfo[6].tableColor}} id="table_7" class="rectangle" onClick={this.TableClick.bind(this, "7")}>Table 7</div>
            </div>

            <div class="table_column table_col2">
                <div class="rectangle2" ></div>
                <div style={{backgroundColor: this.state.tableinfo[5].tableColor}} id="table_6" class="circle" onClick={this.TableClick.bind(this, "6")}>Table 6</div>
                <div style={{backgroundColor: this.state.tableinfo[4].tableColor}} id="table_5" class="circle" onClick={this.TableClick.bind(this, "5")}>Table 5</div>
                <div style={{backgroundColor: this.state.tableinfo[3].tableColor}} id="table_4" class="circle" onClick={this.TableClick.bind(this, "4")}>Table 4</div>
            </div>

            <div class="table_column">
                <div class="rectangle2" ></div>
                <div style={{backgroundColor: this.state.tableinfo[2].tableColor}} id="table_3" class="rectangle" onClick={this.TableClick.bind(this, "3")}>Table 3</div>
                <div style={{backgroundColor: this.state.tableinfo[1].tableColor}} id="table_2" class="rectangle" onClick={this.TableClick.bind(this, "2")}>Table 2</div>
                <div style={{backgroundColor: this.state.tableinfo[0].tableColor}} id="table_1" class="rectangle" onClick={this.TableClick.bind(this, "1")}>Table 1</div>
                {/* <div style={{backgroundColor: 'palegreen'}} id="table_1" class="rectangle" onClick={(e) => this.TClick("1", e)}>Table 1</div> */}
            </div>
        </div>
        return(
            // <Router>
                /* <Link to="/login">login</Link>
                <Link to="/logout">logout</Link>
                <Link to="/order">order</Link>
                <Link to="/paybills">paybills</Link>
                <Link to="/forecast">forecast</Link>
                <Link exact to="/">Home</Link> */
            <div class="table_page">
                {functions}
                {current}
            </div>

            /* <Switch>
                <Route path="/menus">
                <Menu BackTableClick = {this.BackTableClick}
                              // AddFoodClick = {this.AddFoodClick}
                              // AddBtnClick = {this.AddBtnClick}
                              PlaceOrderClick = {this.PlaceOrderClick}
                              // addedList={this.state.addedList}
                              tableNumber = {this.props.tableNumber}
        />
                </Route>
            </Switch>
            </Router>  */
        );
    }
}

export default TableList;
